package com.zbyj.Yazhou.LeftCompanyProgram.CompanyTools;import android.content.Context;import android.util.Log;import com.zbyj.Yazhou.ConfigPageValue.USER_KEY_PAGE;import com.zbyj.Yazhou.LeftCompanyProgram.Config;import com.zbyj.Yazhou.LeftCompanyProgram.Interface.ProgramInterface;import com.zbyj.Yazhou.LeftCompanyProgram.Net;import com.zbyj.Yazhou.LeftCompanyProgram.Tools;import java.io.InputStream;/** * 公司获取用户的信息资料的操作API */public class Usertools {    /**     * 获取用户的所有的资料信息     */    public static void getUservalues(Context mContext, final ProgramInterface programInterface) {        String token = Tools.gettoKen(mContext, USER_KEY_PAGE.KEY_TOKEN);        String phone = Tools.gettoKen(mContext, USER_KEY_PAGE.KEY_USERPHONE);        Net.InterServiceGet(mContext, Config.HTTP_ADDR.getUser_init(), new Net                .onVisitInterServiceListener() {            @Override            public void onSucess(String tOrgin) {                if (programInterface != null) {                    programInterface.onSucess(tOrgin, 0);                }            }            @Override            public void onNotConnect() {                if (programInterface != null) {                    programInterface.onFaile("", 0);                }            }            @Override            public void onFail(String tOrgin) {                if (programInterface != null) {                    programInterface.onFaile("", 0);                }            }        }, Config.HttpMethodUserAction.KEY_ACTION, "" + Config.HttpMethodUserAction.GET_USERVAL,                Config.HttpMethodUserAction.KEY_USER, Tools.getStringMD5(phone), Config                        .HttpMethodUserAction.KEY_TOKEN, token);    }    /**     * 获取用户的默认地址     */    public static void getUserdefaultaddr() {    }    /**     * 获取用户的所有地址     */    public static void getUserAllAddr(Context mContext, final ProgramInterface.XMLforUserAllAddr            xmLforUserAllAddr) {        String phone_md5 = Tools.getStringMD5(Tools.gettoKen(mContext,USER_KEY_PAGE.KEY_USERPHONE));        String token = Tools.gettoKen(mContext,USER_KEY_PAGE.KEY_TOKEN);        Net.getXMLInterGet(mContext, Config.HTTP_ADDR.getallAddr(), new ProgramInterface                .XMLDomServiceInterface() {            @Override            public void onSucess(InputStream is) {                try {                    xmLforUserAllAddr.onDone(Tools.XMLDomeService(is));                } catch (Exception e) {                    Log.e(Config.DEBUG,"Usertools.java[+]XML解析错误" + e.getMessage());                }            }            @Override            public void onFain() {            }            @Override            public void onNotService() {            }        }, Config.HttpMethodUserAction.KEY_ACTION, Config.HttpMethodUserAction.GET_ALLADDR,                Config.HttpMethodUserAction.KEY_USER, phone_md5, Config.HttpMethodUserAction                        .KEY_TOKEN, token);    }}