package com.zbyj.Yazhou.LeftCompanyProgram;import android.util.Log;import android.widget.TextView;/** * program all page */public class ConfigPageClass {    private static AlertViewIDpage AlertViewIDpage;    public ConfigPageClass() {        AlertViewIDpage = new AlertViewIDpage();    }    /**     * get a AlertViewIDpage Instance     *     * @return     */    public static AlertViewIDpage getAlertViewIDpageInstance() {        if (AlertViewIDpage != null) {            Log.i(Config.DEBUG,"数据不为空");            return AlertViewIDpage;        }else{            Log.i(Config.DEBUG,"数据不为空");            return null;        }    }    /**     * have value of AlertViewID     * you must save id for there     */    public class AlertViewIDpage {        private TextView confirm;        private TextView cancle;        private TextView title;        private TextView context;        public TextView getTitle() {            return title;        }        public void setTitle(TextView title) {            this.title = title;        }        public TextView getContext() {            return context;        }        public void setContext(TextView context) {            this.context = context;        }        public TextView getConfirm() {            return confirm;        }        public void setConfirm(TextView confirm) {            this.confirm = confirm;        }        public TextView getCancle() {            return cancle;        }        public void setCancle(TextView cancle) {            this.cancle = cancle;        }    }}