package com.zbyj.Yazhou.ProgramAct;import android.annotation.SuppressLint;import android.os.Bundle;import android.view.View;import android.widget.ImageView;import com.zbyj.Yazhou.R;import com.zbyj.Yazhou.YazhouActivity;public class UserContextAct extends YazhouActivity {    private ImageView btn_allAddrs;    @SuppressLint("ResourceType")    @Override    protected void onCreate(Bundle savedInstanceState) {        super.onCreate(savedInstanceState);        setStatusBar(getResources().getString(R.color.TextAndBodyColor));        setContentView(R.layout.activity_usercontext);        init();    }    /**     * instatnce     */    private void init() {        btn_allAddrs = findViewById(R.id.activity_usercontext_btnPage_addrImage);        Listener();    }    /**     * View listener     */    private void Listener() {        /**         * go to UserAll Addrs list activity         */        btn_allAddrs.setOnClickListener(new View.OnClickListener() {            @Override            public void onClick(View v) {                YaZhouStartActivity(UserAllAddrsAct.class,false);            }        });    }}