package com.zbyj.Yazhou.ProgramAct;import android.annotation.SuppressLint;import android.graphics.Color;import android.graphics.drawable.GradientDrawable;import android.os.Bundle;import android.view.View;import android.widget.ImageView;import android.widget.RelativeLayout;import android.widget.Toast;import com.zbyj.Yazhou.R;import com.zbyj.Yazhou.LeftCompanyProgram.CompanyAct.LeftCompanyAct;public class UserContextAct extends LeftCompanyAct {    private ImageView btn_allAddrs,btn_exchange;    private RelativeLayout rl_headimg;    @SuppressLint("ResourceType")    @Override    protected void onCreate(Bundle savedInstanceState) {        super.onCreate(savedInstanceState);        setStatusBar(getResources().getString(R.color.TextAndBodyColor));        setContentView(R.layout.framelayout_userpage);        init();    }    /**     * instatnce     */    @SuppressLint("ResourceType")    private void init() {        rl_headimg = findViewById(R.id.activity_usercontext_headImg);        GradientDrawable rl_headimgBackground = (GradientDrawable) rl_headimg.getBackground();        rl_headimgBackground.setColor(Color.parseColor(getResources().getString(R.color.TextAndBodyColor)));        rl_headimgBackground.setStroke(5,Color.parseColor(getResources().getString(R.color.TextAndBodyColor)));        btn_allAddrs = findViewById(R.id.activity_usercontext_btnPage_addrImage);        btn_exchange = findViewById(R.id.activity_usercontext_btnExchange);        Listener();    }    /**     * View listener     */    private void Listener() {        btn_exchange.setOnClickListener(new View.OnClickListener() {            @Override            public void onClick(View v) {                Toast.makeText(getApplicationContext(),"1",Toast.LENGTH_SHORT).show();            }        });        /**         * go to UserAll Addrs list activity         */        btn_allAddrs.setOnClickListener(new View.OnClickListener() {            @Override            public void onClick(View v) {                LeftCompanyActStartActivity(ExchangeAct.class,false);            }        });        findViewById(R.id.activity_usercontext_btnPage_btnorderpage).setOnClickListener(new View.OnClickListener() {            @Override            public void onClick(View v) {                Toast.makeText(getApplicationContext(),"1",Toast.LENGTH_SHORT).show();            }        });    }}