package com.zbyj.Yazhou.ProgramAct;import android.annotation.SuppressLint;import android.graphics.Color;import android.graphics.drawable.GradientDrawable;import android.os.Bundle;import android.view.View;import android.widget.ImageView;import android.widget.RelativeLayout;import com.zbyj.Yazhou.R;import com.zbyj.Yazhou.YazhouActivity;public class UserContextAct extends YazhouActivity {    private ImageView btn_allAddrs;    private RelativeLayout rl_headimg;    @SuppressLint("ResourceType")    @Override    protected void onCreate(Bundle savedInstanceState) {        super.onCreate(savedInstanceState);        setStatusBar(getResources().getString(R.color.TextAndBodyColor));        setContentView(R.layout.activity_usercontext);        init();    }    /**     * instatnce     */    @SuppressLint("ResourceType")    private void init() {        rl_headimg = findViewById(R.id.activity_usercontext_headImg);        GradientDrawable rl_headimgBackground = (GradientDrawable) rl_headimg.getBackground();        rl_headimgBackground.setColor(Color.parseColor(getResources().getString(R.color.TextAndBodyColor)));        rl_headimgBackground.setStroke(5,Color.parseColor(getResources().getString(R.color.TextAndBodyColor)));        btn_allAddrs = findViewById(R.id.activity_usercontext_btnPage_addrImage);        Listener();    }    /**     * View listener     */    private void Listener() {        /**         * go to UserAll Addrs list activity         */        btn_allAddrs.setOnClickListener(new View.OnClickListener() {            @Override            public void onClick(View v) {                YaZhouStartActivity(UserAllAddrsAct.class,false);            }        });    }}