package com.zbyj.Yazhou.ProgramAct;import android.annotation.SuppressLint;import android.os.Bundle;import android.view.View;import com.zbyj.Yazhou.LeftCompanyProgram.Config;import com.zbyj.Yazhou.R;import com.zbyj.Yazhou.YazhouActivity;/** * 用户的帮助中心 */public class UserHelpCenter extends YazhouActivity {    @SuppressLint("ResourceType")    @Override    protected void onCreate(Bundle savedInstanceState) {        setContentView(R.layout.activity_userhelpcenter);        setStatusBar(getResources().getString(R.color.TextAndBodyColor));        init();        super.onCreate(savedInstanceState);    }    private void init() {        Listener();    }    private void Listener() {        /**         * 退出按钮         */        findViewById(R.id.activity_userhelpcenter_btnBack).setOnClickListener(new View                .OnClickListener() {            @Override            public void onClick(View v) {                finish();            }        });    }}