package com.zbyj.Yazhou.ProgramAct;import android.annotation.SuppressLint;import android.os.Bundle;import android.view.View;import com.zbyj.Yazhou.LeftCompanyProgram.CompanyPage.WEB_VALUES_ACT;import com.zbyj.Yazhou.R;import com.zbyj.Yazhou.LeftCompanyProgram.CompanyAct.LeftCompanyAct;/** * 用户的帮助中心 */public class UserHelpCenter extends LeftCompanyAct {    @SuppressLint("ResourceType")    @Override    protected void onCreate(Bundle savedInstanceState) {        setContentView(R.layout.activity_userhelpcenter);        setStatusBar(getResources().getString(R.color.TextAndBodyColor));        init();        super.onCreate(savedInstanceState);    }    private void init() {        Listener();    }    private void Listener() {        /**         * 物流帮助信息         */        findViewById(R.id.activity_userhelpcenter_btnFreight).setOnClickListener(new View                .OnClickListener() {            @Override            public void onClick(View v) {                WEB_VALUES_ACT web_values_act = new WEB_VALUES_ACT("http://www.baidu.com");                LeftCompanyActStartWebView(false, web_values_act);            }        });        /**         * 价格相关         */        findViewById(R.id.activity_userhelpcenter_btnPrice).setOnClickListener(new View                .OnClickListener() {            @Override            public void onClick(View v) {                WEB_VALUES_ACT web_values_act = new WEB_VALUES_ACT("http://www.baidu.com");                LeftCompanyActStartWebView(false, web_values_act);            }        });        /**         * 关于取消订单         */        findViewById(R.id.activity_userhelpcenter_btnCancaleOrder).setOnClickListener(new View                .OnClickListener() {            @Override            public void onClick(View v) {                WEB_VALUES_ACT web_values_act = new WEB_VALUES_ACT("http://www.baidu.com");                LeftCompanyActStartWebView(false, web_values_act);            }        });        /**         * 25小时售后服务         */        findViewById(R.id.activity_userhelpcenter_btnSale).setOnClickListener(new View                .OnClickListener() {            @Override            public void onClick(View v) {                WEB_VALUES_ACT web_values_act = new WEB_VALUES_ACT("http://www.baidu.com");                LeftCompanyActStartWebView(false, web_values_act);            }        });        /**         * 账户相关         */        findViewById(R.id.activity_userhelpcenter_btnAccount).setOnClickListener(new View                .OnClickListener() {            @Override            public void onClick(View v) {                WEB_VALUES_ACT web_values_act = new WEB_VALUES_ACT("http://www.baidu.com");                LeftCompanyActStartWebView(false, web_values_act);            }        });        /**         * 版权相关         */        findViewById(R.id.activity_userhelpcenter_btnCopyright).setOnClickListener(new View                .OnClickListener() {            @Override            public void onClick(View v) {                WEB_VALUES_ACT web_values_act = new WEB_VALUES_ACT("http://www.baidu.com");                LeftCompanyActStartWebView(false, web_values_act);            }        });        /**         * 退出按钮         */        findViewById(R.id.activity_userhelpcenter_btnBack).setOnClickListener(new View                .OnClickListener() {            @Override            public void onClick(View v) {                finish();            }        });    }}